import * as types from "../../actionTypes";
import CustomerGroupApi from "../../../api/CustomerGroupApi";

let actionsFunction = {

  //CRUD METHODS
  // Load  list
  loadCustomerGroupList: function() {
    return function(dispatch) {
      return CustomerGroupApi
        .getCustomerGroupList()
        .then(list => {
          dispatch(actionsFunction.loadCustomerGroupListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadCustomerGroupListSuccess: function(list) {
    return { type: types.LIST_CUSTOMERGROUP_SUCCESS, payload: list };
  },


};

export default actionsFunction;
